import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from "react-router-dom";
import LandingPage from "./components/LandingPage";
import Login from "./components/Login";
import Register from "./components/Register";
import MainPage from "./components/MainPage";
import Footer from "./components/Footer";
import Booking from "./components/Booking";
import CostEstimator from "./components/CostEstimator";
import Map from "./components/Map";

const isAuthenticated = () => {
    return localStorage.getItem("authToken") !== null;
};

const ProtectedRoute = ({ element: Component }) => {
    return isAuthenticated() ? <Component /> : <Navigate to="/login" />;
};

function App() {
    return (
        <Router>
            <div className="app-wrapper">
                <RoutesWithFooter />
            </div>
        </Router>
    );
}

// Component to conditionally render Footer based on the current route
function RoutesWithFooter() {
    const location = useLocation();

    // Define routes where footer should not appear
    const noFooterRoutes = ["/login", "/register"];

    return (
        <>
            <Routes>
                <Route path="/" element={<LandingPage />} />
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />
                <Route path="/main" element={<ProtectedRoute element={MainPage} />} />
                <Route path="/booking" element={<ProtectedRoute element={Booking} />} />
                <Route path="/cost-estimator" element={<ProtectedRoute element={CostEstimator} />} />
                <Route path="/map" element={<ProtectedRoute element={Map} />} />
            </Routes>
            {/* Render Footer only if the current route is NOT in noFooterRoutes */}
            {!noFooterRoutes.includes(location.pathname) && <Footer />}
        </>
    );
}

export default App;
